sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
data.needIt=[];
	var gr= new GlideRecord('x_58872_needit_needit');
	gr.query();

	while(gr.next()){
		var needIt_data ={};
		if(gr.priority==1){

			needIt_data.number= gr.getValue('number');
			needIt_data.needed = gr.getDisplayValue('u_when_needed');
			needIt_data.assigned_to = gr.getDisplayValue('assigned_to');
			data.needIt.push(needIt_data);


		}

	}
})();
